let hossz = 400;
let eredetiY;
let ugrik = false;
let ugrasiMagassag = hossz/3.3;
let ugrasiV = 0;
let g = hossz/ 2000;
let jatekVege = false;
//let akadaly = new Akadaly(hossz - hossz / 5, hossz - hossz / 5, hossz / 5, 2);
//let akadaly2 = new Akadaly(1.5 * hossz, hossz - hossz / 10, hossz / 10, 2);
let karakter = new Karakter(hossz / 3, hossz - hossz/16 , hossz/8);
let palya = [
  new Akadaly(0.8 , 0.8, 0.2, 0.005),
  new Akadaly(1.1 , 0.7 , 0.3, 0.005),
  new Akadaly(1.5 , 0.8, 0.2, 0.005),
  new Akadaly(1.8, 0.7 , 0.3, 0.005),
];

function setup() {
  createCanvas(1.5*hossz, hossz);
  eredetiY = karakter.korY;
}

function draw() {
  if (jatekVege) {
    //background("red");
    textSize(30);
    fill("black");
    text("Játék vége!", hossz / 3.5, hossz / 1.8);
    return;
  }
  if (palya[0].akdX == 240) {
    ugrik = true;
  }
  background(220);
  for (let i = 0; i < palya.length; i = i + 1) {
    palya[i].mozgat();
    palya[i].rajzol();
  }
  if (ugrik) {
    karakter.korY += ugrasiV;
    ugrasiV += g;
    if (karakter.korY >= eredetiY) {
      karakter.korY = eredetiY;
      ugrasiV = 0;
      ugrik = false;
    }
  } else if (karakter.korY < eredetiY) {
    karakter.korY += 5;
  }
  karakter.rajzol();
  talaj(karakter, palya);
  vegeVanE(karakter, palya);
  // console.log(
  //  karakter,
  // holVagyunk(karakter, palya[0]),
  //  palya[0],
  //  holVagyunk(karakter, palya[1]),
  //  palya[1]
  //  );
}

function mousePressed() {
  if (karakter.korY >= eredetiY) {
    ugrik = true;
    ugrasiV = -Math.sqrt(2 * g * ugrasiMagassag);
  }
}
// kör paraméterei, akadály paraméterei, ütközött e vagy nem return,



function talaj(kar, paly) {
  let rajtaVanE = false;
  let min = hossz;
  for (let i = 0; i < paly.length; i = i + 1) {
    if (paly[i].holVagyunk(kar) == "rajta") {
      rajtaVanE = true;
      if (min < paly[i].akdY) {
        min = min;
      } else {
        min = paly[i].akdY;
      }
    }
  }
  eredetiY = min - kar.atmero / 2;

  if (rajtaVanE == false) {
    eredetiY = hossz - karakter.atmero / 2;
  }
 // console.log(
   // rajtaVanE,
    //eredetiY,
   // karakter.korY,
    //holVagyunk(karakter, palya[0])
  //);
}

function vegeVanE(kar, paly) {
  for (let i = 0; i < paly.length; i = i + 1) {
    if (paly[i].holVagyunk(kar) == "benne") {
      jatekVege = true;
    }
  }
}
// 48 115
// az utkozes es a kar-akd az akadalyon belul

html, body {
  margin: 0;
  padding: 0;
}
canvas {
  display: block;
}

// Akadály osztály
class Akadaly {
  constructor(akdX, akdY, akd, v) {
    this.akdX = akdX*hossz;
    this.akdY = akdY*hossz;
    this.akd = akd*hossz;
    this.v = v*hossz;
  }

  rajzol() {
    rect(this.akdX, this.akdY, this.akd, this.akd);
  }

  mozgat() {
    this.akdX -= this.v;
  }
  utkozes(kar) {
  //console.log(akd, kar);
  if (
    kar.jobbOldal() > this.akdX &&
    kar.balOldal() < this.akdX + this.akd &&
    kar.also() > this.akdY &&
    kar.felso() < this.akdY + this.akd
  ) {
    return true;
  } else {
    return false;
  }
}
  rajta(kar) {
  if (
    kar.also() <= this.akdY + 5 &&
    kar.jobbOldal() >= this.akdX &&
    kar.balOldal() <= this.akdX + this.akd
    //  && ugrik == false
  ) {
    return true;
  } else {
    return false;
  }
}  
  holVagyunk(kar) {
  if (this.rajta(kar) == true) {
    return "rajta";
  } else if (this.utkozes(kar) == true) {
    return "benne";
  } else {
    return "kivul";
  }
}
}

class Karakter {
  constructor(korX, korY, atmero) {
    this.korX = korX
    this.korY = korY
    this.atmero = atmero
  }
  rajzol() {
    rect(
      this.korX - this.atmero / 2,
      this.korY - this.atmero / 2,
      this.atmero,
      this.atmero
    );
  }
  balOldal() {
    return this.korX - this.atmero / 2;
  }
  jobbOldal() {
    return this.korX + this.atmero / 2;
  }
  also() {
    return this.korY + this.atmero / 2;
  }
  felso() {
    return this.korY - this.atmero / 2;
  }
}
